import React, { useState, useRef } from "react";
import './apartado.css';
import icon from './compressimagen19.jpg';

function Apartado() {
  const textos = {
    1: "My name is joaquin and I'm 20 years old. I'm from Argentina.",
    2: 'My skills are development web sites, programming algorithms.',
    3: 'F.A.S.T.A and cours about english and programming'
  }

  const [texto, setTexto] = useState(textos[1])

  const textFocus = (id) => {
    setTexto(textos[id]);
    setFocusedButton(id);
  }

  const [focusedButton, setFocusedButton] = useState(null);
  
  const handleBlur = () => {
    setFocusedButton(null);
  }

  const buttonRef = useRef(null);

  return (
    <div className="apartado11">
      <div className="fondo-apartado">
        <img className="icon-joaquin" src={icon} alt="joaquinSanchezTonani" />
        <h1 className="name-apartado">I'm Joaquin</h1>
        <div className="div-hire-cv">
          <a className="hire-me" href="#">Hire me</a>
          <a className="cv-download" href="#">Download CV</a>
        </div>
      </div>
      <div className="fondo-apartado2"> 
        <div className="title-apartado-cont">
          <button
            ref={buttonRef}
            id="1"
            onClick={() => textFocus(1)}
            onBlur={handleBlur}
            type="button"
            className={`title-apartado ${focusedButton === 1 ? 'focused' : ''}`}
          >
            About me
          </button>
          <button
            id="2"
            onClick={() => textFocus(2)}
            onBlur={handleBlur}
            type="button"
            className={`title-apartado ${focusedButton === 2 ? 'focused' : ''}`}
          >
            Skills
          </button>
          <button
            id="3"
            onClick={() => textFocus(3)}
            onBlur={handleBlur}
            type="button"
            className={`title-apartado ${focusedButton === 3 ? 'focused' : ''}`}
          >
            Education
          </button>
        </div>
        <div className="contenedor-apartado">
          <p className="p-apartado">{texto}</p>
        </div>
      </div>
    </div>
  );
}

export default Apartado;
