import React, { useState, useRef, useEffect } from "react";
import './apartado.css';

function Apartado() {
  const textos = {
    1: "My name is joaquin and I'm 20 years old. I'm from Argentina.",
    2: 'My skills are development web sites, programming algorithms.',
    3: 'F.A.S.T.A and cours about english and programming'
  }

  const [texto, setTexto] = useState(textos[1])

  const textFocus = (id) => {
    setTexto(textos[id])
  }

  const buttonRef = useRef(null);

  useEffect(() => {
    // Cuando el componente se monta, enfocamos el botón.
    buttonRef.current.focus();
  }, []);

  return (
    <div className="apartado11">
      <div>
        <div className="title-apartado-cont">
          <button
            ref={buttonRef}
            id="1"
            onClick={() => textFocus(1)}
            type="button"
            className="title-apartado"
          >
            About me
          </button>
          <button
            id="2"
            onClick={() => textFocus(2)}
            type="button"
            className="title-apartado"
          >
            Skills
          </button>
          <button
            id="3"
            onClick={() => textFocus(3)}
            type="button"
            className="title-apartado"
          >
            Education
          </button>
        </div>
        <div className="contenedor-apartado">
          <p className="p-apartado">{texto}</p>
        </div>
      </div>
    </div>
  );
}

export default Apartado;
