import React from "react";
import './apartado.css'
import { useState , useRef , useEffect} from "react";
import icon from './compressimagen19.jpg'
import Skills from "../../main/header/body/skills/skills";

function Apartado(){
    const textos = {
        1: "My name is joaquin and I'm 20 years old. I'm from Argentina.",
        2: Skills(),
        3: 'F.A.S.T.A and cours about english and programming'
    }

    const [texto,setTexto] = useState(textos[1])

    const textFocus = (event) =>{
        const id = event.target.id;
        buttonRef.current.classList.remove('enfoque');
        setTexto(textos[id])
    }

    const buttonRef = useRef(null);

    useEffect(() => {
        // Cuando el componente se monta, enfocamos el botón.
        buttonRef.current.classList.add('enfoque');
        
    }, []);
    
    return(
        <div className="apartado11" id="aboutMe">
            <div className="fondo-apartado">
                <div className="div-image">
                    <img className="icon-joaquin" src={icon} alt="joaquinSanchezTonani" />
                </div>
                <h1 className="name-apartado">I'm Joaquin</h1>
                <div className="div-hire-cv">
                    <a className="hire-me" href="#">Hire me</a>
                    <a className="cv-download" href="#">Download CV</a>
                </div>
            </div>
            <div className="fondo-apartado2"> 
                <div className="title-apartado-cont">
                    <button ref={buttonRef} id="1" onFocus={textFocus} type="button" className="title-apartado">About me</button>
                    <button id="2" onFocus={textFocus} type="button" className="title-apartado">Skills</button>
                    <button id="3" onFocus={textFocus} type="button" className="title-apartado">Education</button>
                </div>
                <div className="contenedor-apartado">
                    <p className="p-apartado">{texto}</p>
                </div>
                <div className="redes-sociales2">
                    <a className="redes-link2" rel="noreferrer" target="_blank" href="https://github.com/Joaquin-Sanchez-Tonani"><i className="fi fi-brands-github"></i></a>
                    <a className="redes-link2" rel="noreferrer" target="_blank" href="https://www.linkedin.com/in/joaquin-dario-sanchez-tonani-ba7117272/"><i className="fi fi-brands-linkedin"></i></a>
                    <a className="redes-link2" rel="noreferrer" target="_blank" href="https://www.instagram.com/sanchez.joaq/"><i className="fi animation-link fi-brands-instagram"></i></a>
                </div>
            </div>
        </div>
    )
}

export default Apartado;
